{"output":"// listapps.c  — KPM CTL0：打印 UID>=10000 的进程 pid/uid/comm/nice
#include <compiler.h>
#include <kpmodule.h>
#include <linux/kernel.h>   // scnprintf / snprintf
#include <linux/printk.h>
#include <linux/string.h>
#include <common.h>         // kpver

struct file; typedef long long loff_t;
typedef struct file* (*filp_open_t)(const char *path, int flags, unsigned short mode);
typedef ssize_t (*kernel_read_t)(struct file *file, loff_t *pos, char *buf, size_t size);
typedef void (*filp_close_t)(struct file *file, void *id);

static filp_open_t   p_filp_open;
static kernel_read_t p_kernel_read;
static filp_close_t  p_filp_close;

static inline int parse_dec(const char *s, int *out) {
    long v = 0; int neg = 0, any = 0;
    if (*s == '-') { neg = 1; s++; }
    while (*s >= '0' && *s <= '9') { v = v * 10 + (*s - '0'); s++; any = 1; }
    if (!any) return -1;
    *out = neg ? -(int)v : (int)v; return 0;
}

static ssize_t read_small_file(const char *path, char *buf, size_t bufsz) {
    if (!p_filp_open || !p_kernel_read || !p_filp_close) return -1;
    struct file *f = p_filp_open(path, 0, 0);  // O_RDONLY=0
    if (!f) return -1;
    loff_t pos = 0;
    ssize_t n = p_kernel_read(f, &pos, buf, bufsz - 1);
    p_filp_close(f, NULL);
    if (n < 0) return n;
    buf[n] = '\0'; return n;
}

static unsigned int parse_uid_from_status(const char *status, ssize_t n) {
    const char *p = status, *end = status + n;
    while (p < end) {
        if (!strncmp(p, "Uid:", 4)) {
            const char *q = p + 4; while (q < end && (*q==' '||*q=='\t')) q++;
            int uid; if (parse_dec(q, &uid)==0) return (unsigned int) uid; else return (unsigned int)-1;
        }
        while (p < end && *p != '\n') p++; if (p < end) p++;
    }
    return (unsigned int)-1;
}

static int parse_comm_nice_from_stat(const char *stat, ssize_t n, char *comm_out, size_t comm_sz, int *nice_out) {
    const char *s = stat, *e = stat + n, *l = NULL, *r = NULL;
    for (const char *p = s; p < e; ++p) if (*p == '(') { l = p; break; }
    for (const char *p = e-1; p > s; --p) if (*p == ')') { r = p; break; }
    if (!l || !r || r <= l+1) return -1;
    size_t clen = (size_t)(r - (l+1)); if (clen >= comm_sz) clen = comm_sz - 1;
    memcpy(comm_out, l+1, clen); comm_out[clen] = '\0';
    int field = 3; const char *p = r + 2;
    while (p < e) {
        while (p < e && *p == ' ') p++;
        const char *start = p; while (p < e && *p != ' ') p++;
        field++;
        if (field == 19) { int v; if (parse_dec(start, &v)==0) { *nice_out = v; return 0; } else return -1; }
        if (p < e && *p == ' ') p++;
    }
    return -1;
}

static long ctl0(const char *args, char *out, int outlen) {
    // 绑定符号：用 KP 导出的符号表，通常通过 kfunc 映射到真实内核函数，KPM 允许这么做
    extern void *kv_filp_open, *kv_kernel_read, *kv_filp_close; // 由 KernelPatch 注入
    p_filp_open   = (filp_open_t)   kv_filp_open;
    p_kernel_read = (kernel_read_t) kv_kernel_read;
    p_filp_close  = (filp_close_t)  kv_filp_close;

    char pm_buf[64]; int pid_max = 32768;
    if (read_small_file("/proc/sys/kernel/pid_max", pm_buf, sizeof(pm_buf)) > 0) {
        int tmp; if (parse_dec(pm_buf, &tmp)==0 && tmp > 0) { if (tmp > 500000) tmp = 500000; pid_max = tmp; }
    }
    pr_info("[KPM][listapps] pid_max=%d\n", pid_max);

    int printed = 0;
    for (int pid = 1; pid <= pid_max; ++pid) {
        char path[64], status[2048], stat[1024], comm[256]; int nicev = 0;
        scnprintf(path, sizeof(path), "/proc/%d/status", pid);
        ssize_t n = read_small_file(path, status, sizeof(status));
        if (n <= 0) continue;
        unsigned int uid = parse_uid_from_status(status, n);
        if (uid == (unsigned int)-1 || uid < 10000) continue;
        scnprintf(path, sizeof(path), "/proc/%d/stat", pid);
        n = read_small_file(path, stat, sizeof(stat));
        if (n <= 0) continue;
        if (parse_comm_nice_from_stat(stat, n, comm, sizeof(comm), &nicev) != 0) continue;
        pr_info("[APP] pid=%d uid=%u comm=%s nice=%d\n", pid, uid, comm, nicev);
        printed++;
    }
    if (out && outlen > 0) { scnprintf(out, outlen, "OK %d", printed); out[outlen-1]='\0'; }
    return printed;
}

KPM_NAME("kpm-listapps");
KPM_VERSION("1.0.4");
KPM_LICENSE("Proprietary");
KPM_AUTHOR("you");
KPM_DESCRIPTION("Print app (uid>=10000) processes with nice via /proc");
KPM_CTL0(ctl0);
","metadata":{"exit_code":0,"duration_seconds":0.0}}

