+// 使用 tracepoint 挂载 raw_syscalls:sys_enter，检测 read 并打印
+#include <compiler.h>
+#include <kpmodule.h>
+#include <linux/kernel.h>
+#include <linux/printk.h>
+#include <linux/tracepoint.h>
+#include <linux/ptrace.h>
+#include <common.h>
+
+#define __NR_read 63
+
+// 来自 include/trace/events/syscalls.h 中的 TP_PROTO 定义：
+// TP_PROTO(struct pt_regs *regs, long id)
+// tracepoint 回调原型通常为：void (*fn)(void *ignore, struct pt_regs *regs, long id)
+static void tp_sys_enter(void *ignore, struct pt_regs *regs, long id)
+{
+    if (id == __NR_read) {
+        pr_info("[KPM][ebpf_read] hello read\n");
+    }
+}
+
+// 由内核生成的注册函数原型（trace/events/syscalls.h）：
+extern int register_trace_sys_enter(void (*probe)(void *, struct pt_regs *, long));
+extern int unregister_trace_sys_enter(void (*probe)(void *, struct pt_regs *, long));
+
+static long mod_init(const char *args, const char *event, void *reserved)
+{
+    int rc = register_trace_sys_enter(tp_sys_enter);
+    pr_info("[KPM][ebpf_read] init rc=%d\n", rc);
+    return rc;
+}
+
+static long mod_exit(void *reserved)
+{
+    unregister_trace_sys_enter(tp_sys_enter);
+    pr_info("[KPM][ebpf_read] exit\n");
+    return 0;
+}
+
+KPM_NAME("kpm-ebpf-readtp");
+KPM_VERSION("1.0.0");
+KPM_LICENSE("Proprietary");
+KPM_AUTHOR("you");
+KPM_DESCRIPTION("Trace raw_syscalls:sys_enter and print on read");
+KPM_INIT(mod_init);
+KPM_EXIT(mod_exit);
+
*** End Patch"]}

