{"output":"// 使用 tracepoint 挂载 raw_syscalls:sys_enter，检测 read 并打印
#include <compiler.h>
#include <kpmodule.h>
#include <linux/kernel.h>
#include <linux/printk.h>
#include <linux/ptrace.h>
#include <common.h>
#include <uapi/asm-generic/unistd.h>
#include <syscall.h>
// 移除 /proc 暴露，避免依赖缺失；保留 CTL0 查询接口

static long read_count = 0;
static struct proc_dir_entry *proc_entry;
static int readcount_show(struct seq_file *m, void *v)
{
    seq_printf(m, "%ld\n", read_count);
    return 0;
}

static int readcount_open(struct inode *inode, struct file *file)
{
    return single_open(file, readcount_show, NULL);
}

static const struct proc_ops readcount_ops = {
    .proc_open    = readcount_open,
    .proc_read    = seq_read,
    .proc_lseek   = seq_lseek,
    .proc_release = single_release,
};

static void before_read(hook_fargs3_t *args, void *udata)
{
    pr_info("[KPM][ebpf_read] hello read\n");
    // 非并发安全计数器（演示用），避免依赖编译器内置原子符号
    read_count++;
}

static long mod_init(const char *args, const char *event, void *reserved)
{
    hook_err_t err = inline_hook_syscalln(__NR_read, 3, before_read, 0, 0);
    if (err) {
        pr_err("[KPM][ebpf_read] hook read failed: %d\n", err);
        return -1;
    }
    pr_info("[KPM][ebpf_read] init ok\n");
    // create /proc/kpm_read_count for user-space polling without dmesg
    proc_entry = proc_create("kpm_read_count", 0444, NULL, &readcount_ops);
    return 0;
}

// KernelPatch 提供的安全输出到用户缓冲区的封装
extern long kf_scnprintf(char __user *buf, int outlen, const char *fmt, ...);

static long mod_ctl0(const char *args, char *__user out_msg, int outlen)
{
    long cnt = read_count;
    if (out_msg && outlen > 0)
        kf_scnprintf(out_msg, outlen, "OK read_count=%ld", cnt);
    pr_info("[KPM][ebpf_read] ctl0 read_count=%ld\n", cnt);
    return cnt;
}

static long mod_exit(void *reserved)
{
    inline_unhook_syscalln(__NR_read, before_read, 0);
    if (proc_entry) remove_proc_entry("kpm_read_count", NULL);
    pr_info("[KPM][ebpf_read] exit\n");
    return 0;
}

KPM_NAME("kpm-ebpf-readtp4");
KPM_VERSION("1.0.4");
KPM_LICENSE("Proprietary");
KPM_AUTHOR("you");
KPM_DESCRIPTION("Hook __NR_read and print on read");
KPM_INIT(mod_init);
KPM_CTL0(mod_ctl0);
KPM_EXIT(mod_exit);
","metadata":{"exit_code":0,"duration_seconds":0.0}}

