#include <compiler.h>
#include <kpmodule.h>
#include <linux/kernel.h>
#include <linux/printk.h>
#include <linux/ptrace.h>
#include <common.h>
#include <uapi/asm-generic/unistd.h>
#include <syscall.h>

static long read_count = 0;
/* no /proc exposure; use CTL0 only */

static void before_read(hook_fargs3_t *args, void *udata)
{
    pr_info("[KPM][ebpf_read] hello read\n");
    // 非并发安全计数器（演示用），避免依赖编译器内置原子符号
    read_count++;
}

static long mod_init(const char *args, const char *event, void *reserved)
{
    hook_err_t err = inline_hook_syscalln(__NR_read, 3, before_read, 0, 0);
    if (err) {
        pr_err("[KPM][ebpf_read] hook read failed: %d\n", err);
        return -1;
    }
    pr_info("[KPM][ebpf_read] init ok\n");
    /* no /proc node */
    return 0;
}

static long mod_ctl0(const char *args, char *__user out_msg, int outlen)
{
    long cnt = read_count;
    if (out_msg && outlen > 0)
        kf_scnprintf((char *)out_msg, (size_t)outlen, "OK read_count=%ld", cnt);
    pr_info("[KPM][ebpf_read] ctl0 read_count=%ld\n", cnt);
    return cnt;
}

static long mod_exit(void *reserved)
{
    inline_unhook_syscalln(__NR_read, before_read, 0);
    /* no /proc node to remove */
    pr_info("[KPM][ebpf_read] exit\n");
    return 0;
}

