+    private static List<Txn> listTransactions(String descriptor) throws Exception {
+        // descriptor is usually the interface FQN, e.g. android.app.IActivityManager
+        String stubName = descriptor + "$Stub";
+        Class<?> stub = Class.forName(stubName);
+        List<Txn> out = new ArrayList<>();
