+            getName = stub.getDeclaredMethod("getDefaultTransactionName", int.class);
+        } catch (Throwable ignored) {}
+        for (Field f : stub.getDeclaredFields()) {
+            if ((f.getModifiers() & java.lang.reflect.Modifier.STATIC) == 0) continue;
+            if (f.getType() != int.class) continue;
+            String fn = f.getName();
+            if (!fn.startsWith("TRANSACTION_")) continue;
+            f.setAccessible(true);
+            int code = f.getInt(null);
+            String name = null;
+            if (getName != null) {
+                try { name = (String) getName.invoke(null, code); } catch (Throwable ignored) {}
+            }
+            if (name == null) {
+                name = fn.substring("TRANSACTION_".length());
+            }
+            out.add(new Txn(code, name));
+        }
+        return out;
+    }
