+    private static JSONObject intrude(String service, String descriptor, int code) throws Exception {
+        JSONObject res = new JSONObject();
+        res.put("service", service);
+        res.put("descriptor", descriptor);
+        res.put("code", code);
+        IBinder b = ServiceManager.getService(service);
+        if (b == null) {
+            res.put("result", "NO_SERVICE");
+            return res;
+        }
+        Parcel data = Parcel.obtain();
+        Parcel reply = Parcel.obtain();
+        try {
+            data.writeInterfaceToken(descriptor);
+            boolean ok = b.transact(code, data, reply, 0);
+            res.put("ok", ok);
+            // Try to read exception if any
+            try {
+                reply.readException();
+                res.put("result", "NO_EXCEPTION");
+            } catch (Throwable te) {
+                res.put("result", te.getClass().getSimpleName());
+                res.put("error", String.valueOf(te));
+            }
+        } catch (SecurityException se) {
+            res.put("result", "SECURITY_EXCEPTION");
+            res.put("error", String.valueOf(se));
+        } catch (Throwable t) {
+            res.put("result", t.getClass().getSimpleName());
+            res.put("error", String.valueOf(t));
+        } finally {
+            reply.recycle();
+            data.recycle();
